maintenance.scss:
* {
  box-sizing: border-box;
}
body {
  background: #888 url(../img/coming-soon.jpg) no-repeat center center;
  background-size: cover;
  color: #798289;
  font-family: Helvetica, Arial, Sans-Serif;
  font-size: .8em;
  height: 100vh;
  line-height: 1.5em;
  margin: 0;
  min-width: 320px;
  min-height: 480px;
  padding: 20px 30px;

  -ms-box-orient: horizontal;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;

  -webkit-justify-content: space-around;
  -ms-flex-pack: space-around;
  justify-content: space-around;

  -webkit-align-items: center;
  -ms-flex-align: center;
  align-items: center;
}
main {
  box-shadow: 0 2px 0 0 rgba(0,0,0,0.07);
  background-color: #fff;
  display: block;
  padding: 20px 30px;
  text-align: center;
}
section {
  margin-bottom: 40px;
}
fieldset {
  border: none;
  padding: 0;
}
legend {
  overflow: hidden;
  position: absolute !important;
  width: 1px;
  height: 1px;
  margin: -1px;
  padding: 0;
  border: 0;
  clip: rect(0 0 0 0);
}
label, input {
  display: block;
  height: 30px;
  line-height: 30px;
  width: 100%;
}
label {
  color: #313b47;
  font-size: 14px;
  font-weight: bold;
  text-align: center;
}
input {
  border-radius: 2px;
  border-style:solid;
  width: 100%;
  -webkit-appearance: none;
}
input[type=text] {
  background-color: #F7F8F9;
  border: 1px solid #E1E6EB;
  margin-bottom: 1rem;
  padding: 0 1em;
}
input[type=submit] {
  cursor: pointer;
  background: #00abc9;
  border: none;
  color: #fff;
  font-size: 12px;
  padding: 0 15px;
}
a {
  text-decoration: none;
  color: #00b9d3;
  margin: 0 5px;
}
h1 {
  color: #3C4043;
  font-weight: 300;
  font-size: 24px;
  margin-top: 15px;
}
div p {
  margin-top: 0;
  margin-bottom: 0;
}
.form-field--error {
  margin-bottom: 1rem;
}
.form-field--error input[type=text] {
  margin-bottom: 0;
}
.form-field--error .form-inlineMessage{
  color: #ac0005;
  margin-top: .375rem;
  width: 100%;
}
.powered-by {
  color: #acb3be;
  font-size: 11px;
  font-style: italic;
  line-height: 24px;
}
.powered-by a {
  background: url('../img/bc_logo_sprite.png') 0 -75px no-repeat;
  display: inline-block;
  height: 22px;
  opacity: .7;
  text-indent: -9999px;
  width: 95px;
}
.powered-by a:hover {
  opacity: 1;
}
@media screen and (min-width: 540px) {
  body {
    font-size: 1em;
  }
  main {
    margin: 0 auto;
    padding: 45px 60px;
    width: 500px;
  }
  label, input {
    display: inline-block;
    margin-right: 10px;
    width: auto;
  }
  label {
    text-align: left;
  }
  input[type=text] {
    margin-right: 10px;
    margin-bottom: 0;
  }
  .form-field {
    display: inline-block;
    position: relative;
  }
  .form-field--error .form-inlineMessage {
    /*color: #ac0005;*/
    left: 100px;
    position: absolute;
    top: 30px;
    /*width: 100%;*/
  }
}

assets/scss/custom-joy/custom.scss:
body {
    color: $text_color;
}

div {
    &.body {
        padding-bottom: 100px;
        @include media('laptop') {
            padding-bottom: 70px;
        }
    }
}

h1, h2, h3, h4, h5, h6 {
    font-weight: 500;
}

.container {
    max-width: 1260px;
    padding: 0 20px; 

    @include media('laptop') {
        max-width: 100%;
    }
}

div#sticky_addtocart {
    display: none;
}

.custom-joy-btn {
    padding: 10px 30px;
    background: $primary_color;
    color: $white_color;
    border-radius: 10px; 
    font-size: 15px; 
    line-height: 24px;  
    display: inline-block;
    @include media('mobile') {
        font-size: 14px; 
        line-height: 20px;  
    }

    &:hover {
        color: $white_color;
    }

    &.transparent-btn {
        border: 1px solid #D9D9D9;
        background-color: transparent;
        color: $secondary_color;
    }

    &.with-arrow {
        padding: 10px 60px 10px 30px;
        color: $white_color;
        background-image: url("data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='10' cy='10' r='10' fill='white'/%3E%3Cpath d='M11.2461 6.8877L14.3589 10.0005L11.2461 13.1133' stroke='%23DB0032' stroke-miterlimit='10' stroke-linecap='round' stroke-linejoin='round'/%3E%3Cpath d='M5.6416 10.001H14.2724' stroke='%23DB0032' stroke-miterlimit='10' stroke-linecap='round' stroke-linejoin='round'/%3E%3C/svg%3E%0A");
        background-repeat: no-repeat;
        background-position: right 30px center; 
        @include media('mobile') {
            padding: 10px 50px 10px 20px; 
            background-position: right 20px center; 
        }
        &:hover {
            background-color: $secondary_color ; 
            background-image: url("data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='10' cy='10' r='10' fill='white'/%3E%3Cpath d='M11.2461 6.8877L14.3589 10.0005L11.2461 13.1133' stroke='black' stroke-miterlimit='10' stroke-linecap='round' stroke-linejoin='round'/%3E%3Cpath d='M5.6416 10.001H14.2724' stroke='black' stroke-miterlimit='10' stroke-linecap='round' stroke-linejoin='round'/%3E%3C/svg%3E%0A");
        }
    } 
    
}


.themevale_breadcrumb {
    .breadcrumbs {
        .breadcrumb {
            color: $secondary_color;
            font-size: 14px;
            line-height: 18px;
            a {
                color: $secondary_color;
                &:hover {
                    color: $primary_color;
                }
            }
            
            
            &.is-active {
               .breadcrumb-label {
                   color: $primary_color;
               } 
            }
        }
    }
}

/*** Product Page Style ***/

.slick-dots {
    position: static;
    display: flex;
    justify-content: center;
    margin-top: 20px;
    column-gap: 10px;
    li {
        display: flex;
        margin: 0;
        button {
            width: 12px;
            height: 8px;
            border-radius: 10px;
            background: #DADADA;
            transition: all ease-in 0.4s;
            border: 0;
        }
        &.slick-active {
            button {
                border: 0;
                width: 30px;
                background: $primary_color;
            }
        }
    }
}

.productView {
    &.joy-productview {
        display: flex;
        margin-bottom: 117px;
        @include media('laptop') {  
            margin-bottom: 70px;
        }
        @include media('mobile') {  
            flex-direction: column;
            row-gap: 0;
            margin-bottom: 60px;
        }

        .slick-dots {
            position: static;
        }

        .productView-images { 
            .themevale_badges {
                top: 20px;
                right: 20px;
                @include media('laptop') { 
                    margin: 0;
                    border-width: 10px;
                    top: 10px;
                    right: 10px;
                } 
                .new-badge {
                    background-color: $primary_color;
                }
            }
        }

        .productView-rating {

             
            .icon {
                height: 20px;
                width: 20px;
            }

            .productView-reviewLink {
                font-size: 14px;
                line-height: 18px;
                @include media('ipad') {
                    margin-left: 8px;
                }
                @include media('mobile') {
                    margin-left: 0px;
                }
            }
        }

        .themevale_productViewImage {
            flex-direction: column;

            .hover-to-zoom {
                display: none;
            }

            .productView-image {
                width: 699px;
                height: 100%;
                max-height: 676px;
                background: $gray_color; 
                border-radius: 10px;
                margin-bottom: 30px;
                border: 20px solid $gray_color;

                @include media('laptop') { 
                    margin: 0;
                    border-width: 10px;
                }

                .productView-img-container {
                    max-width: 100%;
                    img {
                        height: 636px;
                        width: 659px;
                    }
                }
            }

            .productView-thumbnails-wrappers {
                max-width: calc(100% + 30px);
                margin: 0 -15px;
                @include media('laptop') {
                    max-width: calc(100% + 20px);
                    margin: 0 -10px;
                }
            }

            .productView-thumbnails {
                order: 2;
                width: 100%;
                padding: 0;
                opacity: 0;
                transition: opacity ease-in 0.4s;
                &.slick-initialized {
                    opacity: 1;
                }

                .productView-thumbnail { 
                    padding: 0 15px;
                    @include media('laptop') {
                        padding: 0 10px;
                    }
                    .productView-thumbnail-link {
                        background: $gray_color;
                        border-radius: 10px;
                        height: 143px;
                        width: 152px;

                        img {
                            border-radius: 10px;
                            width: calc(100% - 18px);
                            max-height: 86%;
                        }
                    }
                }
            }
        }

        .themevale_productView-left {
            width: 57.378%;
            @include media('laptop') {
                width: 50%;
            }
            @include media('mobile') {  
                width: 100%;
            }

            .productView-image-mobile {
                @include media('mobile') {  
                    margin: 0;
                }

                .slick-slide {
                    > div { 
                        @include media('mobile') {  
                            display: flex;
                        }
                    }
                }
            }
            .productView-thumbnails-mobile {
                @include media('mobile') {  
                   display: none;
                }
            }
        }

        .themevale_productView-right {
            width: 42.622%;
            position: relative;
            @include media('laptop') {
                width: 50%;
                padding-left: 20px;
                margin: 0;
            }
            @include media('mobile') {  
                width: 100%;
                padding-left: 0px;
                margin-top: 30px;
            }

            p {
                @include media('mobile') {  
                    font-size: 14px;
                    line-height: 20px;
                }
            }

            br {
                @include media('mobile') {  
                    display: none;
                }
            }

            .productView-brand, .productView-brand a { 
                font-size: 14px;
                font-weight: 400;
                line-height: 24px; 
                color: #62707C;
            }

            .product-title-header {
                padding-right: 48px;
            }

            .productView-title {
                font-size: 32px;
                font-weight: 500;
                color: #161A1D;
                line-height: 42px;
                @include media('laptop')  {
                    font-size: 26px;
                    line-height: 32px;
                }
                @include media('ipad')  {
                    font-size: 22px;
                    line-height: 30px;
                }
            }

            .productView-rating {
                margin-bottom: 20px;
                display: flex;
                align-items: center;
                @include media('mobile')  {
                   flex-direction: column;
                   row-gap: 6px;
                   align-items: flex-start;
                }
            }

            .productView-price {
                .price { 
                    font-size: 27px;
                    font-weight: 500;
                    line-height: 37px; 
                    @include media('laptop') { 
                        font-size: 24px; 
                        line-height: 30px; 
                    }
                    @include media('mobile') { 
                        font-size: 20px; 
                        line-height: 26px; 
                    }
                }
            }

            .short-description {
                font-size: 16px;
                font-weight: 400;
                line-height: 26px;
                color: #62707C;
                @include media('laptop') {
                    font-size: 14px;
                    line-height: 20px;
                }
            }

            .productView-info {
                .productView-info-wrapper {
                    display: flex; 
                    column-gap: 4px;

                    + .productView-info-wrapper {
                        margin-top: 10px;
                        @include media('laptop') {
                            margin-top: 8px;
                        }
                    }
                    .productView-info-name,
                    .productView-info-value { 
                        font-size: 16px; 
                        line-height: 26px; 
                        color: #000000;
                        @include media('laptop') {
                            font-size: 14px;
                            line-height: 20px;
                        }
                    }
                    .productView-info-value {
                        float: none;
                        color: #62707C;
                    }
                    .productView-info-name {
                        min-width: max-content;
                        margin: 0;
                        font-weight: 500;
                        text-transform: capitalize;
                    }
                }
            }  


            .productView-options {
                position: static;
                .form-field--increments {
                    .form-label { 
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 24px;  
                        margin-bottom: 10px;
                        color: #000;
                    }
                }
                .form-increment {
                    &.joy-cart-custombtn {
                        display: inline-flex;
                        align-items: center;
                        border-color: #D9D9D9;
                        border-radius: 6px;
                       
                        .form-input { 
                            font-size: 18px;
                            font-weight: 400;
                            line-height: 28px; 
                            color: $secondary_color;
                            width: 64px;
                        }

                        .increment-wrapper {
                            display: flex;
                            flex-direction: column; 
                            padding: 3px 6px 3px 6px; 
                            flex-direction: column;
                            padding: 3px 6px;
                            justify-content: center;
                            border-left: 1px solid #D9D9D9;
                            min-width: 40px;
                            align-items: center; 

                            .button {
                                height: 20px;
                                width: 20px;
                                display: flex;
                                border: 0;
                                .icon {
                                    padding: 0;
                                }
                                svg {
                                    fill: #929292;
                                    color: #929292;;
                                } 
                            }
                        }
                    }
                }
            } 
        }

        #add-to-cart-wrapper {
            .card-wishlist {
                width: max-content !important; 
                position: absolute;
                top: 0;
                right: 0;

                .wishlist-menu {
                    border: 1px solid #333;
                    border-radius: 10px;
                    margin-top: 11px;
                    right: -10px;

                    .wishlist-item {
                        margin-top: 6px;
