.joy-custom-video-block {
    background: #F7F7F7;
    padding: 100px 0;
    margin-bottom: 100px;
    @include media('laptop') {
        padding: 70px 0;
        margin-bottom: 70px;    
    }
    @include media('mobile') {
        padding: 60px 0;
        margin-bottom: 60px;    
    }

    .joy-custom-video-inner {
        margin: 0 -15px;
        display: flex;
        align-items: center;
        @include media('laptop') { 
            margin: 0 -10px;
        }
        @include media('mobile') { 
            flex-direction: column;
            row-gap: 30px;
        }
        > div { 
            width: 50%;
            padding: 0 15px;
            @include media('laptop') { 
                padding: 0 10px;
            }
            @include media('mobile') { 
                width: 100%;
            }
           
            .joy-custom-video-inner-block {
                height: 450px;
                width: 100%;
                background: $white_color;
                border-radius: 10px;
                position: relative;
                @include media('mobile') { 
                    height: 76.92vw;
                }

                video, iframe {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                }

                .video-play-icon {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%,-50%);
                }
            }
            
            &.joy-custom-video-content { 
                
        
                .joy-custom-video-content-text {
                    margin-bottom: 40px;
                    color: #62707C;
                    @include media('laptop') { 
                        margin-bottom: 20px;
                    }
                    p { 
                        font-size: 16px;
                        font-weight: 400;
                        line-height: 26px; 
                        @include media('laptop') { 
                            font-size: 14px; 
                            line-height: 22px; 
                        }
                        &:last-child {
                            margin-bottom: 0;
                        }
                    }
                }
            }
        }
    } 
}


