.card {
    background: #F5F3F4;
    padding: 20px;
    border-radius: 10px;
    height: 100%;
    margin: 0;
    @include flex-direction(column);
    display: flex !important;

    .card-img-container {
        height: 220px;
    }

    .card-figure {
        border-radius: 10px;
    }

    .card-body { 
        @include display-flex;
        @include flex-direction(column); 
        @include justify-content(flex-start); 
        text-align: center;

        .card-wrapper {
            justify-content: center;
            margin-bottom: 20px;
            .card-price {
                .price-section {
                    .price {
                        font-size: 16px;
                        line-height: 24px;
                        font-weight: 600;
                        color: #333333;
                    }
                }
            } 
        }

        .card-figcaption {
            margin-top: auto;
            height: auto;
            margin-bottom: 0 !important;
            > .button {
                padding: 8px 30px;
                border-radius: 10px;
                background: $primary_color;
                color: $white_color; 
                font-size: 15px;
                font-weight: 400;
                line-height: 24px; 
                display: inline-block;
                width: auto;
                border: none;
                text-transform: capitalize;
                &:hover {
                    background-color: $secondary_color;
                }
            }
        } 
        
        .card-title, .card-title a { 
            font-size: 22px;
            font-weight: 600;
            line-height: 32px;
            color: $secondary_color;
            margin: 0;
            text-align: center;

            @include media('laptop') {
                font-size: 16px;
                line-height: 24px;
            }

            &:hover {
                color: $primary_color;
            }
        }

        .card-title {
            margin-bottom: 10px;
        }

        .card-brand {
            font-size: 16px;
            font-weight: 400;
            line-height: 26px; 
            color: #62707C;
            margin-bottom: 5px;
        }
       
    }
}

