optimized-checkout.scss:
// =============================================================================
// Stencil Checkout - Customize the Optimized Single-Page Checkout experience
// =============================================================================

// =============================================================================
//
// IMPORTANT DISCLAIMER
// Please do not nest elements within class selectors, and do not use class selectors other than those given below.
// Future support is guaranteed only if class selectors' structure and naming are left unchanged.
//
// =============================================================================

//
// Header Section
// Background, Logo Position
// -----------------------------------------------------------------------------
@import url('https://fonts.googleapis.com/css?family=Arimo:400,600,700|Rajdhani:400,600,700');

*:focus {
    outline: none;
    outline-color: transparent;
}

a {
    position: relative;
    text-decoration: none;
    transition: all 0.3s ease;

    &:hover,
    &:focus,
    &:active {
        outline: none;
        text-decoration: none;
        box-shadow: none;
    }
}

.optimizedCheckout-header {
    color: #383e47;
    text-align: left;
    min-height: 0;
    border-bottom: none;
    background-color: #ffffff;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, .06), 0 1px 2px 0 rgba(0, 0, 0, .1);

    .checkoutHeader-link img {
        max-height: 24px;
    }

    .checkoutHeader-content {
        margin-bottom: 0;
        padding: 15px 0 15px 0;
    }

    .checkoutHeader-heading {
        text-align: left;
        font-size: 24px;
        line-height: 1;
    }

    @media (min-width: 551px) {
        .checkoutHeader-link img {
            max-height: 40px;
        }

        .checkoutHeader-content {
            padding: 25px 0 25px 0;
        }

        .checkoutHeader-heading {
            font-size: 36px;
        }
    }

    @media (min-width: 1230px) {
        .checkoutHeader-content {
            padding: 25px 15px;
        }
    }
}

//
// Primary and Secondary Headings
// Text Color & Typography
// .optimizedCheckout-headingPrimary styles top-level headings.
// .optimizedCheckout-headingSecondary styles certain lower-level elements, such as descriptions of cart items.
// -----------------------------------------------------------------------------

.optimizedCheckout-headingPrimary {
    font-weight: 700;
    font-family: "Rajdhani", Arial, Helvetica, sans-serif;
    color: stencilColor('color-textHeading');
}

.optimizedCheckout-headingSecondary {
    font-weight: 700;
    font-family: "Rajdhani", Arial, Helvetica, sans-serif;
    color: stencilColor('color-textHeading');
}

//
// Body
// Background & Shipping Method Overlay
// -----------------------------------------------------------------------------

body,
.ng-checkout-container {
    font-size: 12px;
    font-weight: 400;
    line-height: 22px;
    font-family: "Arimo", Arial, Helvetica, sans-serif;
    color: stencilColor('color-textBase');
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-weight: 700;
    color: stencilColor('color-textHeading');
    font-family: "Rajdhani", Arial, Helvetica, sans-serif;
}

p,
ul,
ol,
dl {
    font-size: 12px;
    line-height: 22px;
    font-weight: 400;
    color: stencilColor('color-textBase');
}

.optimizedCheckout-overlay {
    min-height: 0;
    color: #383e47;
    font-weight: 700;
    border-radius: 0;
    background-color: #fafafa;
    border-color: #fafafa;

    p {
        color: #383e47;
    }
}

//
// Primary & Secondary Content
// Text Color & Typography
// .optimizedCheckout-contentPrimary styles body text in the Order Summary/Order Confirmation; drop-down-list items; and text that summarizes completed steps.
// .optimizedCheckout-contentSecondary styles check boxes' text labels, and lower-level text in the Order Summary.
// -----------------------------------------------------------------------------

.optimizedCheckout-contentPrimary {
    font-weight: 400;
    font-family: "Arimo", Arial, Helvetica, sans-serif;
    color: stencilColor('color-textBase');
}

.optimizedCheckout-contentSecondary {
    font-weight: 400;
    font-family: "Arimo", Arial, Helvetica, sans-serif;
    color: stencilColor('color-textBase');
}

//
// Primary Button
// Background & Border & Text Color & Typography
// Styles the CONTINUE button for each step, & the final confirmation button.
// -----------------------------------------------------------------------------

.optimizedCheckout-buttonPrimary {
    line-height: 1;
    font-size: 12px;
    text-align: center;
    border-radius: 3px;
    position: relative;
    padding: 13px 15px 11px 15px;
    margin-bottom: 0;
    letter-spacing: 0;
    text-transform: uppercase;
    transition: all 0.3s ease;
    font-family: "Rajdhani", Arial, Helvetica, sans-serif;
    font-weight: 700;
    color: #ffffff;
    border: 1px solid #383e47;
    background-color: #383e47;

    &.is-loading {
        // color: #383e47;
        // background-color: #383e47;
    }

    &:hover,
    &:focus,
    &:active {
        outline: none;
        box-shadow: none;
        border: #383e47;
    }

    @media (min-width: 415px) {
        font-size: 16px;
        padding: 12px 15px 10px 15px;
    }

    @media (min-width: 1025px) {

        &:hover,
        &:focus,
        &:active {
            color: #383e47;
            border: 1px solid #383e47;
            background-color: transparent;
        }
    }

    &[disabled],
    &[disabled]:hover,
    &[disabled]:focus,
    &[disabled].is-active,
    &[disabled].active {
        opacity: 0.7;
        border: 1px solid #383e47;
        // background-color: #383e47;
    }

    &[disabled].is-loading {
        opacity: 0.7;
        // color: #383e47;
        // background-color: #383e47;
    }
}

.button {
    &.dropdown-toggle--select {
        min-height: 40px;
        padding: 10px 15px;
        line-height: 20px;
        font-family: "Rajdhani", Arial, Helvetica, sans-serif;

        .checkout-address--static {
            font-family: "Arimo", Arial, Helvetica, sans-serif;
        }
    }
}


//
// Secondary Button
// Background & Border & Text Color & Typography
// Styles the EDIT button for each step.
// -----------------------------------------------------------------------------

.optimizedCheckout-buttonSecondary {
    color: #383e47;
    font-size: 16px;
    padding: 12px 15px 10px 15px;
    border-radius: 3px;
    line-height: 1;
    font-weight: 700;
    min-width: 120px;
    border: 1px solid #383e47;
    text-transform: uppercase;
    background-color: transparent;
    box-shadow: none;
    font-family: "Rajdhani", Arial, Helvetica, sans-serif;

    &:hover,
    &:focus,
    &:active {
        box-shadow: none;
        color: #383e47;
        border: 1px solid #383e47;
        background-color: transparent;
    }

    &[disabled],
    &[disabled]:hover,
    &[disabled]:focus,
    &[disabled].is-active,
    &[disabled].is-loading,
    &[disabled].active {
        color: #383e47;
        border: 1px solid #383e47;
        background-color: transparent;
    }

    svg {
        fill: #383e47;
        stroke: #383e47;
    }
}

//
// Order Summary (Mobile Drawer & Modal)
// Background Color
// -----------------------------------------------------------------------------

.optimizedCheckout-orderSummary {
    margin: 0;
    border-radius: 3px;
    border: 1px solid #e2e4e8;
    background-color: #fff;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.05);

    .cart-header {
        padding: 15px 20px 13px 20px;
        background-color: #f7f7f7;

        .cart-title {
            font-size: 20px;
            font-weight: 700;
            text-transform: uppercase;
        }

        .cart-header-link {
            font-size: 16px;
            color: #005599;
            font-weight: 600;
            text-transform: uppercase;
            font-family: "Rajdhani", Arial, Helvetica, sans-serif;
        }

        +cart-items .cart-section {
            border-top: none;
        }
    }

    .cart-section {
        padding: 20px;
        border-color: #eeeeee;

        .cart-section-heading {
            color: #2d2d2d;
            line-height: 1;
            font-size: 12px;
            margin-bottom: 20px;
        }

        .productList-item {
            margin-bottom: 30px;

            &:last-child {
                margin-bottom: 10px;
            }
        }

        .product {
            .product-figure {
                width: 90px;
                padding-right: 20px;
            }

            .product-body {
                padding: 0;
                width: calc(75% - 90px);
            }

            .product-title {
                font-size: 12px;
                line-height: 22px;
                margin-bottom: 10px;
                color: #005599;
                font-weight: 400;
                font-family: "Arimo", Arial, Helvetica, sans-serif;

            }

            .product-options {
                font-size: 12px;
                line-height: 25px;
                font-style: italic;
                color: #8c8c8c;
            }

            .product-price {
                font-size: 14px;
                font-weight: 700;
                color: #141414;
            }
        }

        .productImage-giftCertificate {
            padding-top: 0;
        }

        .cart-priceItem {
            margin-bottom: 15px;

            .cart-priceItem-label {
                font-size: 16px;
                text-transform: uppercase;
                color: stencilColor('color-textHeading');
                font-weight: 700;
                font-family: "Rajdhani", Arial, Helvetica, sans-serif;
            }

            .cart-priceItem-value {
                font-size: 14.5px;
                color: stencilColor('color-textHeading');
                font-weight: 700;
            }
        }

        .redeemable-label {
            font-size: 16px;
            font-weight: 700;
            text-transform: uppercase;
            font-family: "Rajdhani", Arial, Helvetica, sans-serif;
            color: stencilColor('color-textHeading');
        }

        .cart-priceItem--total {
            margin-bottom: 0;

            .cart-priceItem-label {
                font-size: 14.5px;
                color: stencilColor('color-textHeading');
                font-weight: 700;

            }

            .cart-priceItem-value {
                font-size: 24px;
                color: #141414;
                font-weight: 700;

            }
        }
    }

    .optimizedCheckout-buttonSecondary {
        padding: 0;
        font-weight: 400;
        border: none;

        &:hover,
        &:focus,
        &:active {
            border: none;
        }
    }
}

.form-prefixPostfix {
    .button {
        height: auto;
        min-width: 120px;
        line-height: 1;
        font-size: 16px;
        text-align: center;
        border-radius: 3px;
        position: relative;
        padding: 12px 5px 10px 5px;
        margin-bottom: 0;
        text-transform: uppercase;
        transition: all 0.3s ease;
        color: #ffffff;
        font-weight: 700;
        font-family: "Rajdhani", Arial, Helvetica, sans-serif;
        border: 1px solid #383e47;
        background-color: #383e47;

        @media (min-width: 1025px) {

            &:hover,
            &:focus,
            &:active {
                color: #383e47;
                border: 1px solid #383e47;
                background-color: #ffffff;
            }
        }
    }
}

//
// Form Labels
// Text Color
// Styles text labels on form fields.
// -----------------------------------------------------------------------------

.optimizedCheckout-form-label {
    font-size: 12px;
    color: stencilColor('color-textHeading');
    font-weight: 700;
    margin-bottom: 10px;

    small {
        color: #646464;
        font-weight: 400;
    }
}

.form-field--error {

    .optimizedCheckout-form-label,
    .form-inlineMessage {
        color: #d14343;
    }
}

//
// Form Input
// Background, Border, Shadow and Text Color
// Styles form fields.
// -----------------------------------------------------------------------------

$optimizedCheckout-input-boxShadow: inset 0 1px 1px #e5e5e5;
$optimizedCheckout-input--focus-boxShadow: 0 0 3px transparentize(#005599, 0.6);
$optimizedCheckout-input--error-boxShadow: 0 0 3px transparentize(#d14343, 0.6);

.optimizedCheckout-form-input,
.optimizedCheckout-form-select {
    height: 40px;
    font-size: 12px;
    padding: 10px 15px;
    line-height: 20px;
    border-radius: 3px;
    box-shadow: none;
    color: stencilColor('color-textBase');
    background-color: #ffffff;
    border-color: #dcdcdc;

    &::placeholder {
        color: #999999;
    }

    .form-field--error & {
        background-color: #ffffff;
        border-color: #d14343;
        box-shadow: $optimizedCheckout-input--error-boxShadow, $optimizedCheckout-input-boxShadow;
    }
}

.optimizedCheckout-form-input:focus,
.optimizedCheckout-form-select:focus,
.optimizedCheckout-form-select:hover {
    box-shadow: none;
    border-color: #005599;
    background-color: #ffffff;
}

.optimizedCheckout-form-label::before {

    .optimizedCheckout-form-radio+&,
    .optimizedCheckout-form-checkbox+& {
        opacity: 1;
        border-color: #dcdcdc;
        background-color: #ffffff;
        box-shadow: $optimizedCheckout-input-boxShadow;
    }

    .optimizedCheckout-form-radio:checked+&,
    .optimizedCheckout-form-checkbox:checked+& {
        background-color: #005599;
        border-color: #005599;
        box-shadow: none;
    }

    .optimizedCheckout-form-radio:focus+&,
    .optimizedCheckout-form-checkbox:focus+& {
        border-color: #005599;
        box-shadow: $optimizedCheckout-input--focus-boxShadow, $optimizedCheckout-input-boxShadow;
    }

    .optimizedCheckout-form-radio:focus:checked+&,
    .optimizedCheckout-form-checkbox:focus:checked+& {
        box-shadow: $optimizedCheckout-input--focus-boxShadow;
    }
}

//
// Discount Banners
// Background & Text & Icon Color
// Styles discount banners.
// -----------------------------------------------------------------------------

.optimizedCheckout-discountBanner {
    color: #383e47;
    background-color: #e5e5e5;

    svg {
        fill: #383e47;
    }
}

//
// Form Checklist (Shipping / Payment methods)
// Background & Border & Text Color
// Styles form checklists
// -----------------------------------------------------------------------------

.optimizedCheckout-form-checklist {
    color: #383e47;
    border-radius: 0;
    border: 1px solid #ebebeb;
    background-color: #f7f7f7;
    border-radius: 4px;

    .form-checklist-checkbox~.form-label {
        font-size: 14px;
        min-height: 0;
        font-weight: 500;
    }
}

.optimizedCheckout-form-checklist-item {
    border-color: #ebebeb;
    background-color: #f7f7f7;
    border-radius: 4px;
}

.optimizedCheckout-form-checklist-item:hover,
.optimizedCheckout-form-checklist-item--selected {
    border-color: #ebebeb;
    background-color: #f7f7f7;
    border-radius: 4px;
}

.optimizedCheckout-form-checklist-checkbox {

    ~.form-label::after,
    ~.form-label::before {
        background-color: #ffffff;
        border-color: #dcdcdc;
    }

    &:focus~.form-label {
        box-shadow: inset 0 0 0 1px #005599;
    }

    &:checked~.form-label::before {
        border-color: #005599;
    }

    &:checked~.form-label::after {
        background: #005599;
    }
}

//
// Loading Toaster
// Background & Text Color
// Styles the Loading Toaster
// -----------------------------------------------------------------------------

.optimizedCheckout-loadingToaster {
    color: #ffffff;
    background-color: #383e47;
}

.cart-note {
    padding-left: 0;
    font-style: italic;
    color: #8c8c8c;
    text-align: center;

    @media (min-width: 993px) {
        margin-top: 10px;
    }
}

.cart-priceItem--totalNote {
    color: #8c8c8c;
    font-style: italic;
}

.redeemable-payments {
    display: block;
    margin-top: 10px;
}

.redeemable-label {
    color: #005599;
}

/*=========================================
=            www.themevale.com            =
=========================================*/

/*--------  1.0 Global style  -------*/

.form-checkbox+.form-label {
    font-weight: 400;
    color: stencilColor('color-textBase');

    &:before {
        border-radius: 3px;
    }
}

.form-field {
    margin-bottom: 20px;
}

a[data-test="forgot-password-link"] {
    display: block;
    margin-top: 10px;
}

.alertBox-message {

    strong,
    b {
        line-height: inherit;
        display: inline-block;
        vertical-align: middle;
    }
}

.layout {
    padding: 60px 15px;

    .layout-main {
        width: 60%;
        padding: 0 30px 0 0;

        .checkout-steps {
            padding-right: 0;
        }
    }

    .layout-cart {
        width: 40%;
        padding: 0;
    }
}

.checkout-steps {
    >.checkout-step {
        padding: 0px;
        margin-bottom: 25px;
        border: none;

        +.checkout-step {
            padding-top: 25px;
            border-top: 1px solid #eeeeee;
        }

        &[checkout-step="customer"],
        &[checkout-step="shipping"],
        &[checkout-step="billing"] {
            .optimizedCheckout-buttonPrimary {
                min-width: 180px;
            }
        }
    }
}

.stepHeader {
    padding: 0;
    flex-wrap: wrap;
    position: relative;

    .stepHeader-figure {
        width: 100%;
        display: block;
        height: auto;
        margin-right: 0;

        .stepHeader-counter {
            width: 45px;
            height: 45px;
            border-radius: 50%;
            color: #ffffff;
            font-size: 20px;
            font-weight: 400;
            line-height: 42px;
            position: relative;
            margin-right: 15px;
            display: inline-block;
            vertical-align: middle;
            background-color: #383e47;
            -webkit-transform: scale(1);
            transform: scale(1);

            &.stepHeader-counter--complete {
                transform: scale(1);
            }
        }

        .stepHeader-title {
            margin: 0;
            padding: 0;
            font-size: 24px;
            min-width: 350px;
            display: inline-block;
            vertical-align: middle;
            text-transform: uppercase;
        }
    }

    .stepHeader-body {
        margin-top: 20px;
        margin-left: 65px;

        .checkout-address--static .address-entry {
            margin-bottom: 5px;
        }
    }

    .stepHeader-actions {
        position: absolute;
        right: 0;
        top: 8px;
        margin: 0;
        z-index: 10;
    }

    .stepHeader-counter--complete svg {
        padding: 10px;
    }
}

.checkout-view-header .stepHeader {
    display: inline-block;
    width: 100%;
    padding-top: 0;
    padding-bottom: 0;
    flex-wrap: wrap;
    position: relative;

    .stepHeader-row {
        flex-wrap: wrap;
    }

    .stepHeader-figure {
        align-items: center;
        align-content: center;
        width: 100%;
        display: block;
        height: auto;
        margin-right: 0;

        .stepHeader-counter {
            width: 45px;
            height: 45px;
            border-radius: 50%;
            color: #ffffff;
            font-size: 20px;
            font-weight: 400;
            line-height: 45px;
            position: relative;
            margin-right: 15px;
            display: inline-block;
            vertical-align: middle;
            background-color: #383e47;
            transform: scale(1);

            &:before {
                width: 45px;
                height: 45px;
                line-height: 45px;
            }

            &.stepHeader-counter--complete {
                transform: scale(1);

                &:before {
                    width: 0;
                    height: 0;
                }
            }
        }

        .stepHeader-title {
            margin: 0;
            padding: 0;
            font-size: 20px;
            display: inline-block;
            vertical-align: middle;
            text-transform: capitalize;
        }
    }

    .stepHeader-body {
        margin-top: 0;
        margin-left: 0;

        @media (min-width: 1080px) {
            margin-left: 65px;
        }

        >.customerView,
        >.staticConsignmentContainer,
        >.checkout-address--static {
            margin-top: 25px;
        }

        .optimizedCheckout-contentPrimary {
            line-height: 1.9;
            color: stencilColor("optimizedCheckout-headingSecondary-color");
        }

        .first-name,
        .family-name {
            color: stencilColor("optimizedCheckout-headingSecondary-color");
            font-weight: 700;
        }

        p {
            line-height: 1.9;
        }

        .shippingOption-desc,
        .shippingOption-price {
            font-size: stencilNumber('fontSize-root') + 2;
            line-height: 1.9;
        }

        .checkout-address--static .address-entry {
            margin-bottom: 5px;
        }
    }

    .stepHeader-actions {
        position: absolute;
        right: 0;
        top: 8px;
        margin: 0;
        z-index: 10;
    }

    .stepHeader-counter--complete svg {
        padding: 10px;
    }
}
