.checkout-step--payment {
    .checkout-view-content {
        margin: 30px 0 25px 0;
    }
}

#checkout-shipping-comments {
    .optimizedCheckout-form-input {
        min-height: 75px;
    }
}

#checkout-payment-continue {
    font-size: 16px;
    color: #ffffff;
    border-color: #b20000;
    background-color: #b20000;
    padding: 16px 30px 15px 30px;
}

.form-checklist-body {
    .form-checklist-container {
        padding-top: 0;
        padding-bottom: 10px;

        p {
            margin-bottom: 0;
        }
    }
}

#checkout-shipping-options {
    margin-bottom: 20px;
}

.orderConfirmation {
    padding-top: 0;

    h1[data-test="order-confirmation-heading"] {
        margin-bottom: 30px;
    }

    #createAccountButton {
        min-width: 200px;
    }
}

.socialBadges-header {
    margin-top: 1.5rem;
}

.customerEmail-action {
    margin-top: 28px;
}


.layout,
.optimizedCheckout-header {
    padding-left: 15px;
    padding-right: 15px;
}

@media (max-width: 992px) {
    .layout {
        .layout-main {
            padding: 0;
            width: 100%;
        }

        .layout-cart {
            padding: 0;
            width: 100%;
        }
    }

    .checkout-view-header .stepHeader {
        .stepHeader-figure {
            .stepHeader-counter {
                width: 35px;
                height: 35px;
                line-height: 35px;
                margin-right: 10px;
                font-size: 16px;

                &:before {
                    width: 35px;
                    height: 35px;
                    line-height: 35px;
                    font-size: 16px;
                }
            }

            .stepHeader-title {
                font-size: 16px;
            }
        }

        .stepHeader-actions {
            top: 4px;

            .optimizedCheckout-buttonSecondary {
                text-transform: capitalize;
            }
        }

        .stepHeader-body {
            margin-top: 0;
        }
    }

    .checkout-view-content {
        margin-top: 25px;
    }

    .checkout-steps {
        >.checkout-step {
            margin-bottom: 30px;

            +.checkout-step {
                padding-top: 30px;
            }
        }
    }

    .customerView-actions {
        .optimizedCheckout-buttonSecondary {
            text-transform: capitalize;
        }
    }
}

@media (max-width: 767px) {
    .optimizedCheckout-header {
        .checkoutHeader-link img {
            max-height: 30px;
        }

        .checkoutHeader-content {
            padding: 20px 0;
        }

        .checkoutHeader-heading {
            text-align: left;
            font-size: 30px;
            line-height: 1;
        }
    }

    .checkout-view-header .stepHeader .stepHeader-body,
    .checkout-view-content .checkout-form {
        margin-left: 0;
    }

    .layout {
        padding-bottom: 90px;
    }

    #checkout-shipping-comments .optimizedCheckout-form-input {
        min-height: 0;
    }

    .orderConfirmation {
        h1[data-test="order-confirmation-heading"] {
            font-size: 26px;
            margin-bottom: 20px;
        }

        .form-body {
            margin-bottom: 0;
        }
    }
}

@media (max-width: 550px) {
    .customerEmail-body {
        width: 100%;
    }

    .customerEmail-action {
        width: 100%;
        padding-left: 0;
        margin-top: 15px;
    }

    .optimizedCheckout-orderSummary:not(.modal-header):not(.modal-body) {
        margin-left: 15px;
        margin-right: 15px;
        margin-bottom: 15px;
    }

    #checkout-payment-continue {
        padding: 10px 15px;
        font-size: 13px;
    }

    .cart-modal-body {
        padding-left: 0;
        padding-right: 0;
    }

    .checkout-view-header .stepHeader {
        .stepHeader-figure {
            .stepHeader-title {
                min-width: 0;
                font-size: 15px;
            }
        }
    }

    .cartDrawer-body {
        padding-left: 15px;

        .cartDrawer-items {
            font-size: 14px;
        }
    }

    .cartDrawer-total {
        font-size: 18px;
    }

    .form-prefixPostfix .button {
        min-width: 90px;
    }

    .orderConfirmation {
        .orderConfirmation-section {
            margin-bottom: 20px;
            padding-bottom: 20px;
        }
    }

    .optimizedCheckout-orderSummary {
        margin: 0;

        .cart-section .product {
            .product-figure {
                width: 75px;
                padding-right: 15px;
            }

            .product-body {
                width: calc(75% - 75px);
            }

            .product-actions {
                align-self: flex-start;
                -ms-flex-order: 3;
                order: 3;
                text-align: right;
                width: 25%;
            }
        }
    }
}

@media (max-width: 414px) {
    .optimizedCheckout-orderSummary {
        margin: 0;

        .cart-section .product {
            .product-figure {
                position: absolute;
            }

            .product-body {
                width: 100%;
                margin-left: 75px;

                .product-title {
                    margin-bottom: 3px;
                }
            }

            .product-actions {
                align-self: flex-start;
                -ms-flex-order: 3;
                order: 3;
                text-align: left;
                width: 100%;
                margin-left: 75px;
                margin-top: 5px;
            }
        }

        .cart-section .productList-item {
            min-height: 80px;
        }
    }
}

/*=====  End of www.themevale.com  ======*/

assets/scss/custom-joy/custom.scss:
body {
    color: $text_color;
}

div {
    &.body {
        padding-bottom: 100px;
        @include media('laptop') {
            padding-bottom: 70px;
        }
    }
}

h1, h2, h3, h4, h5, h6 {
    font-weight: 500;
}

.container {
    max-width: 1260px;
    padding: 0 20px; 

    @include media('laptop') {
        max-width: 100%;
    }
}

div#sticky_addtocart {
    display: none;
}

.custom-joy-btn {
    padding: 10px 30px;
    background: $primary_color;
    color: $white_color;
    border-radius: 10px; 
    font-size: 15px; 
    line-height: 24px;  
    display: inline-block;
    @include media('mobile') {
        font-size: 14px; 
        line-height: 20px;  
    }

    &:hover {
        color: $white_color;
    }

    &.transparent-btn {
        border: 1px solid #D9D9D9;
        background-color: transparent;
        color: $secondary_color;
    }

    &.with-arrow {
        padding: 10px 60px 10px 30px;
        color: $white_color;
        background-image: url("data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='10' cy='10' r='10' fill='white'/%3E%3Cpath d='M11.2461 6.8877L14.3589 10.0005L11.2461 13.1133' stroke='%23DB0032' stroke-miterlimit='10' stroke-linecap='round' stroke-linejoin='round'/%3E%3Cpath d='M5.6416 10.001H14.2724' stroke='%23DB0032' stroke-miterlimit='10' stroke-linecap='round' stroke-linejoin='round'/%3E%3C/svg%3E%0A");
        background-repeat: no-repeat;
        background-position: right 30px center; 
        @include media('mobile') {
            padding: 10px 50px 10px 20px; 
            background-position: right 20px center; 
        }
        &:hover {
            background-color: $secondary_color ; 
            background-image: url("data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='10' cy='10' r='10' fill='white'/%3E%3Cpath d='M11.2461 6.8877L14.3589 10.0005L11.2461 13.1133' stroke='black' stroke-miterlimit='10' stroke-linecap='round' stroke-linejoin='round'/%3E%3Cpath d='M5.6416 10.001H14.2724' stroke='black' stroke-miterlimit='10' stroke-linecap='round' stroke-linejoin='round'/%3E%3C/svg%3E%0A");
        }
    } 
    
}


.themevale_breadcrumb {
    .breadcrumbs {
        .breadcrumb {
            color: $secondary_color;
            font-size: 14px;
            line-height: 18px;
            a {
                color: $secondary_color;
                &:hover {
                    color: $primary_color;
                }
            }
            
            
            &.is-active {
               .breadcrumb-label {
                   color: $primary_color;
               } 
            }
        }
    }
}

/*** Product Page Style ***/

.slick-dots {
    position: static;
    display: flex;
    justify-content: center;
    margin-top: 20px;
    column-gap: 10px;
    li {
        display: flex;
        margin: 0;
        button {
            width: 12px;
            height: 8px;
            border-radius: 10px;
            background: #DADADA;
            transition: all ease-in 0.4s;
            border: 0;
        }
        &.slick-active {
            button {
                border: 0;
                width: 30px;
                background: $primary_color;
            }
        }
    }
}

.productView {
    &.joy-productview {
        display: flex;
        margin-bottom: 117px;
        @include media('laptop') {  
            margin-bottom: 70px;
        }
        @include media('mobile') {  
            flex-direction: column;
            row-gap: 0;
            margin-bottom: 60px;
        }

        .slick-dots {
            position: static;
        }

        .productView-images { 
            .themevale_badges {
                top: 20px;
                right: 20px;
                @include media('laptop') { 
                    margin: 0;
                    border-width: 10px;
                    top: 10px;
                    right: 10px;
                } 
                .new-badge {
                    background-color: $primary_color;
                }
            }
        }

        .productView-rating {

             
            .icon {
                height: 20px;
                width: 20px;
            }

            .productView-reviewLink {
                font-size: 14px;
                line-height: 18px;
                @include media('ipad') {
                    margin-left: 8px;
                }
                @include media('mobile') {
                    margin-left: 0px;
                }
            }
        }

        .themevale_productViewImage {
            flex-direction: column;

            .hover-to-zoom {
                display: none;
            }

            .productView-image {
                width: 699px;
                height: 100%;
                max-height: 676px;
                background: $gray_color; 
                border-radius: 10px;
                margin-bottom: 30px;
                border: 20px solid $gray_color;

                @include media('laptop') { 
                    margin: 0;
                    border-width: 10px;
                }

                .productView-img-container {
                    max-width: 100%;
                    img {
                        height: 636px;
                        width: 659px;
                    }
                }
            }

            .productView-thumbnails-wrappers {
                max-width: calc(100% + 30px);
                margin: 0 -15px;
                @include media('laptop') {
                    max-width: calc(100% + 20px);
                    margin: 0 -10px;
                }
            }

            .productView-thumbnails {
                order: 2;
                width: 100%;
                padding: 0;
                opacity: 0;
                transition: opacity ease-in 0.4s;
                &.slick-initialized {
                    opacity: 1;
                }

                .productView-thumbnail { 
                    padding: 0 15px;
                    @include media('laptop') {
                        padding: 0 10px;
                    }
                    .productView-thumbnail-link {
                        background: $gray_color;
                        border-radius: 10px;
                        height: 143px;
                        width: 152px;

                        img {
                            border-radius: 10px;
                            width: calc(100% - 18px);
                            max-height: 86%;
                        }
                    }
                }
            }
        }

        .themevale_productView-left {
            width: 57.378%;
            @include media('laptop') {
                width: 50%;
            }
            @include media('mobile') {  
                width: 100%;
            }

            .productView-image-mobile {
                @include media('mobile') {  
                    margin: 0;
                }

                .slick-slide {
                    > div { 
                        @include media('mobile') {  
                            display: flex;
                        }
                    }
                }
            }
            .productView-thumbnails-mobile {
                @include media('mobile') {  
                   display: none;
                }
            }
        }

        .themevale_productView-right {
            width: 42.622%;
            position: relative;
            @include media('laptop') {
                width: 50%;
                padding-left: 20px;
                margin: 0;
            }
            @include media('mobile') {  
                width: 100%;
                padding-left: 0px;
                margin-top: 30px;
            }

            p {
                @include media('mobile') {  
                    font-size: 14px;
                    line-height: 20px;
                }
            }

            br {
                @include media('mobile') {  
                    display: none;
                }
            }

            .productView-brand, .productView-brand a { 
                font-size: 14px;
                font-weight: 400;
                line-height: 24px; 
                color: #62707C;
            }

            .product-title-header {
                padding-right: 48px;
            }

            .productView-title {
                font-size: 32px;
                font-weight: 500;
                color: #161A1D;
                line-height: 42px;
                @include media('laptop')  {
                    font-size: 26px;
                    line-height: 32px;
                }
                @include media('ipad')  {
                    font-size: 22px;
                    line-height: 30px;
                }
            }

            .productView-rating {
                margin-bottom: 20px;
                display: flex;
                align-items: center;
                @include media('mobile')  {
                   flex-direction: column;
                   row-gap: 6px;
                   align-items: flex-start;
                }
            }

            .productView-price {
                .price { 
                    font-size: 27px;
                    font-weight: 500;
                    line-height: 37px; 
                    @include media('laptop') { 
                        font-size: 24px; 
                        line-height: 30px; 
                    }
                    @include media('mobile') { 
                        font-size: 20px; 
                        line-height: 26px; 
                    }
                }
            }

            .short-description {
                font-size: 16px;
                font-weight: 400;
                line-height: 26px;
                color: #62707C;
                @include media('laptop') {
                    font-size: 14px;
                    line-height: 20px;
                }
            }

            .productView-info {
                .productView-info-wrapper {
                    display: flex; 
                    column-gap: 4px;

                    + .productView-info-wrapper {
                        margin-top: 10px;
                        @include media('laptop') {
                            margin-top: 8px;
                        }
                    }
                    .productView-info-name,
                    .productView-info-value { 
                        font-size: 16px; 
                        line-height: 26px; 
                        color: #000000;
                        @include media('laptop') {
                            font-size: 14px;
                            line-height: 20px;
                        }
                    }
                    .productView-info-value {
                        float: none;
                        color: #62707C;
                    }
                    .productView-info-name {
                        min-width: max-content;
                        margin: 0;
                        font-weight: 500;
                        text-transform: capitalize;
                    }
                }
            }  


            .productView-options {
                position: static;
                .form-field--increments {
                    .form-label { 
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 24px;  
                        margin-bottom: 10px;
                        color: #000;
                    }
                }
                .form-increment {
                    &.joy-cart-custombtn {
                        display: inline-flex;
                        align-items: center;
                        border-color: #D9D9D9;
                        border-radius: 6px;
                       
                        .form-input { 
                            font-size: 18px;
                            font-weight: 400;
                            line-height: 28px; 
                            color: $secondary_color;
                            width: 64px;
                        }

                        .increment-wrapper {
                            display: flex;
                            flex-direction: column; 
                            padding: 3px 6px 3px 6px; 
                            flex-direction: column;
                            padding: 3px 6px;
                            justify-content: center;
                            border-left: 1px solid #D9D9D9;
                            min-width: 40px;
                            align-items: center; 

                            .button {
                                height: 20px;
                                width: 20px;
                                display: flex;
                                border: 0;
                                .icon {
                                    padding: 0;
                                }
                                svg {
                                    fill: #929292;
                                    color: #929292;;
                                } 
                            }
                        }
                    }
                }
            } 
        }

        #add-to-cart-wrapper {
            .card-wishlist {
                width: max-content !important; 
                position: absolute;
                top: 0;
                right: 0;

                .wishlist-menu {
                    border: 1px solid #333;
                    border-radius: 10px;
                    margin-top: 11px;
                    right: -10px;

                    .wishlist-item {
                        margin-top: 6px;
                        padding-left: 15px;
                        padding-right: 15px;
                        &:last-child {
                            border-top: 1px solid #333;
                            padding-top: 5px;
                            padding-top: 10px;
                            background: #F5F3F4;
                        }
                        &:first-child {
                            margin-top: 0;
                        }
                        a {
                            padding: 0;
                            text-transform: capitalize;
                            font-size: 14px;
                            line-height: 20px;
                            &:hover {
                                color: $primary_color;
                            }
                        }
                    }
                }

                &.has-product {
                    .button { 
                        background-color: transparent;
                        background-image: url("data:image/svg+xml,%3Csvg width='16' height='14' viewBox='0 0 16 14' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M7.868 14C7.64395 14 7.42801 13.9188 7.25967 13.7714C6.62396 13.2155 6.01109 12.6932 5.47035 12.2324L5.46758 12.23C3.88224 10.879 2.51324 9.71231 1.56072 8.563C0.49596 7.27821 0 6.05996 0 4.72913C0 3.43613 0.443374 2.24324 1.24834 1.37008C2.06292 0.486587 3.18064 0 4.39597 0C5.30432 0 6.13618 0.287182 6.86843 0.853494C7.23795 1.13932 7.57289 1.48917 7.868 1.89723C8.1632 1.48917 8.49805 1.13932 8.8677 0.853494C9.59994 0.287182 10.4318 0 11.3402 0C12.5554 0 13.6732 0.486587 14.4878 1.37008C15.2927 2.24324 15.736 3.43613 15.736 4.72913C15.736 6.05993 15.2402 7.27818 14.1754 8.56287C13.2228 9.71231 11.854 10.8789 10.2689 12.2298C9.72718 12.6913 9.11336 13.2145 8.4762 13.7716C8.30782 13.9189 8.0917 14.0001 7.868 14Z' fill='%23DB0032'/%3E%3C/svg%3E%0A");                    
                    }
                }

