        }

        .joy_product_actions_btns_wrapper {
            display: flex;
            margin: 0 -10px;
            @include media('ipad') {  
                flex-direction: column;
                row-gap: 15px;
            }
            > div {
                width: 50%;
                padding: 0 5.5px;
                margin: 0;
                @include media('ipad') {  
                    width: 100%;
                }

