                }
                
                input {
                    margin: 0;
                    width: 100% !important; 
                    font-size: 15px;
                    font-weight: 400;
                    line-height: 24px; 
                    border: 0;
                    padding: 8px 30px  !important; 
                    box-shadow: none !important;
                    height: auto;
                    min-height: inherit;
                    background-color: $primary_color;
                    color: $white_color;
                    border-radius: 10px;
                    text-transform: capitalize  !important;

                    &:hover {
                        background-color: $secondary_color;
                        color: $white_color;
                    }
                }
            }
        }
    } 
}

.custom-joy-expert {
    margin-bottom: 100px;
    display: none;
}

.themevale_productDescription  {
    &.custom-joy-description {
        margin-bottom: 64px;
        @include media('laptop') {  
            margin-bottom: 35px;
        }

        @include media('mobile') {  
            margin-bottom: 25px;
        }

        .tabs-contents {
            .tab-content {
                padding: 0;
                margin-bottom: 10px;    
                border-top: 0;            

                .toggle-title {
                    padding: 12px 16px;
                    background: #f5f6f7; 
                    border-radius: 10px;

                    .toggleLink { 
                        .page-heading {  
                            font-size: 18px;
                            font-weight: 600;
                            line-height: 26px;
                            @include media('mobile') {  
                                font-size: 16px; 
                                line-height: 24px;
                            }
                        }
                    }
                }
            }
        }

        .tabs {
            display: flex;
            justify-content: center;
            @media screen and (max-width: 1024px) {
                display: none;
            }

            .tab {
                &.is-active {
                    a {
                        background-color: $primary_color;
                        border-color: $primary_color;
                        color: $white_color;
                    }
                }
                a { 
                    margin: 0 10px;
                    font-size: 16px;
                    line-height: 26px;
                    font-weight: 500; 
                    padding: 16px 60px;
                    min-width: 308px;
                    color: #62707C;
                    border: 1px solid #CECECE;
                    border-bottom: 0;
                    background: #F5F5F5;
                    border-top-left-radius: 5px;
                    border-top-right-radius: 5px;

                    &:after {
                        display: none;
                    }
                }
            } 
        }

        .productView-description {
            width: 100%;
        }
        #tab-specifications{
            .product_desc_image {
                width: 100%;
                height: 100%;
            }
        }
        .product_desc_image {
            // background: #F5F3F4;
            border-radius: 6px;
            // padding: 13px;
            width: 387px;
            float: left;
            height: 313px;
            margin-right: 30px;
            margin-bottom: 30px;
            @include media('laptop') { 
                width: 300px; 
                height: 240px;
                margin-right: 20px;
                margin-bottom: 20px;
                padding: 10px;
            }
            img {
                height: 100%;
                width: 100%;
                // object-fit: cover;
                border-radius: 3px;
            }
        }

        .product_desc_content { 
            font-size: 16px;
            font-weight: 400;
            line-height: 26px;  
            color: #62707C;
            @include media('laptop') {
                font-size: 14px;   
                line-height: 20px;
            }
          
        

            ul {
                margin: 0;
                li {
                    display: flex;
                    align-items: flex-start;
                    column-gap: 10px;
                
                    &:before {
                        content: "";    
                        height: 8px;
                        width: 100%;
                        max-width: 8px;
                        background: #000;
                        border-radius: 50%;
                        display: inline-block;
                        margin-top: 9px;
                
                        @include media('laptop') {
                            margin-top: 8px;
                            height: 6px;
                            max-width: 6px;
                        }
                
                        @include media('mobile') {
                            min-width: 6px;
                        }
                    }
                
                    &::marker {
                        height: 8px;
                        width: 8px;
                    }
                
                    + li {
                        margin-top: 10px;
                    }
                
                    // &:last-child::before {
                    //     width: 9.5px;
                    // }
                }
                
            }

            span {
                &.h3 {
                    font-size: 24px;
                    line-height: 30px; 
                    margin-bottom: 20px;
                    display: inline-block;
                    border-left: 3px solid #DB0032;
                    padding: 4px 0 4px 16px;
                }
            }
        }
    }

}

.custom-joy-related-slider-main {
    .toggle-title {
        margin: 0;
    }
    .toggle-content {
        padding: 0;
    }
    .section-title  {
        .page-heading {
            margin-bottom: 50px;
            @include media('laptop') {
                margin-bottom: 30px;
            }
        }
    }
    .toggle  {
        &.productRelated {
            padding: 0;
            margin: 0;
            border: 0;
        }
    }
}

.joy-custom-video-block {
    background: #F7F7F7;
    padding: 100px 0;
    margin-bottom: 100px;
    @include media('laptop') {
        padding: 70px 0;
        margin-bottom: 70px;    
    }
    @include media('mobile') {
        padding: 60px 0;
        margin-bottom: 60px;    
    }

    .joy-custom-video-inner {
        margin: 0 -15px;
        display: flex;
        align-items: center;
        @include media('laptop') { 
            margin: 0 -10px;
        }
        @include media('mobile') { 
            flex-direction: column;
            row-gap: 30px;
        }
        > div { 
            width: 50%;
            padding: 0 15px;
            @include media('laptop') { 
                padding: 0 10px;
            }
            @include media('mobile') { 
                width: 100%;
            }
           
            .joy-custom-video-inner-block {
                height: 450px;
                width: 100%;
                background: $white_color;
                border-radius: 10px;
                position: relative;
                @include media('mobile') { 
                    height: 76.92vw;
                }

                video, iframe {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                }

                .video-play-icon {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%,-50%);
                }
            }
            
            &.joy-custom-video-content { 
                
        
                .joy-custom-video-content-text {
                    margin-bottom: 40px;
                    color: #62707C;
                    @include media('laptop') { 
                        margin-bottom: 20px;
                    }
                    p { 
                        font-size: 16px;
                        font-weight: 400;
                        line-height: 26px; 
                        @include media('laptop') { 
                            font-size: 14px; 
                            line-height: 22px; 
                        }
                        &:last-child {
                            margin-bottom: 0;
                        }
                    }
                }
            }
        }
    } 
}


.section-title {
    .page-heading { 
        font-size: 40px;
        font-weight: 600;
        line-height: 50px;  
        padding-left: 20px;
        border-left: 3px  solid $primary_color;
        margin: 0 0 30px;
        max-width: 440px;
        text-transform: none;
        color: #000000;
        @include media('laptop') {
            font-size: 30px; 
            line-height: 36px;  
            max-width: 330px;
            margin-bottom: 20px;
        }
        @include media('mobile') {
            font-size: 24px; 
            line-height: 30px;    
            padding-left: 14px;
        }
    }
}


.custom-joy-related-slider-main {
    .productCarousel {
        &.slick-slider {
            margin-bottom: 0;
            .slick-dots {
                margin-top: 40px;
                @include media('mobile') { 
                    margin-top: 24px;
                }
            }
        }
        
        .slick-track {
            display: flex; 
        }
       
        .slick-slide {
            padding: 0;
            height: auto;
            > div {
                padding: 0 15px;
                height: 100%;
                @include media('laptop') {
                    padding: 0 10px;    
                }
            }
        }
    }
}


.card {
    background: #F5F3F4;
    padding: 20px;
    border-radius: 10px;
    height: 100%;
    margin: 0;
    @include flex-direction(column);
    display: flex !important;

    .card-img-container {
        height: 220px;
    }

    .card-figure {
        border-radius: 10px;
    }

    .card-body { 
        @include display-flex;
        @include flex-direction(column); 
        @include justify-content(flex-start); 
        text-align: center;

        .card-wrapper {
            justify-content: center;
            margin-bottom: 20px;
            .card-price {
                .price-section {
                    .price {
                        font-size: 16px;
                        line-height: 24px;
                        font-weight: 600;
                        color: #333333;
                    }
                }
            } 
        }

        .card-figcaption {
            margin-top: auto;
            height: auto;
            margin-bottom: 0 !important;
            > .button {
                padding: 8px 30px;
                border-radius: 10px;
                background: $primary_color;
                color: $white_color; 
                font-size: 15px;
                font-weight: 400;
                line-height: 24px; 
                display: inline-block;
                width: auto;
                border: none;
                text-transform: capitalize;
                &:hover {
                    background-color: $secondary_color;
                }
            }
        } 
        
        .card-title, .card-title a { 
            font-size: 22px;
            font-weight: 600;
            line-height: 32px;
            color: $secondary_color;
            margin: 0;
            text-align: center;

            @include media('laptop') {
                font-size: 16px;
                line-height: 24px;
            }

            &:hover {
                color: $primary_color;
            }
        }

        .card-title {
            margin-bottom: 10px;
        }

        .card-brand {
            font-size: 16px;
            font-weight: 400;
            line-height: 26px; 
            color: #62707C;
            margin-bottom: 5px;
        }
       
    }
}

.product-details-btns {
    display: flex;
    margin: 0 -10px 20px;
    @include media('ipad') {
       flex-direction: column;
       row-gap: 15px;
    }
    @include media('mobile') { 
        flex-direction: column;
    }
    .product-details-btn {
        width: 50%;
        padding: 0 5.5px;
        @include media('ipad') { 
            width: 100%;
        }
        .custom-joy-btn {
            width: 100%;
            text-align: center;
            border-color: $secondary_color;
            color: $secondary_color;
            padding: 7px 30px;

            &:hover { 
                background-color: $secondary_color;
                color: $white_color;
            }
            @include media('ipad') { 
                padding: 7px 20px;
            }
            
        }
    }
}
