                                                {{else}}
                                                    <option value="{{id}}">&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash; {{name}}</option>
                                                {{/if}}
                                            {{/each}}
                                        {{/if}}
                                    {{/each}}
                                {{/if}}
                            {{/each}}
                        {{/if}}
                    {{/each}}
                </select> 
                <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
            </div>
            <input class="form-input" data-search-quick name="search_query" id="search_query" data-error-message="{{lang 'search.error.empty_field'}}" placeholder="{{lang 'search.quick_search.input_quickSearch'}}" autocomplete="off">
            <button class="btn-search" type="submit"><svg class="icon"><use xlink:href="#icon-search"></use></svg><span>Search</span></button>
        </div>
    </fieldset>
</form>
